ScriptGroup.script:
require "MuMeM-Core"

-- --- Module setup ----------------------------------------------

MuMeM = MuMeM or {}
MuMeM.Skill = MuMeM.Skill or {}


-- --- Module registration -----------------------------------------

MuMeM.Skill.module_name = "MuMeM.Skill"
MuMeM.Skill.module_dependencies = {"MuMeM.Core","MuMeM.Log"}
MuMeM.modules = MuMeM.modules or {}
MuMeM.modules[MuMeM.Skill.module_name] = MuMeM.modules[MuMeM.Skill.module_name] or {module=MuMeM.Skill}

Script.script:
function MuMeM.Skill.initialize()
  MuMeM.Skill.Repository.initialize()
end

function MuMeM.Skill.configure()
  MuMeM.Skill.Repository.configure()
end

function MuMeM.Skill.skillNameToUid(skillName)
  return string.lower(string.gsub(skillName, " ", "_"))
end

MuMeM.Skill.Repository = MuMeM.Skill.Repository or { _skills = {} }
function MuMeM.Skill.Repository.initialize()
end
function MuMeM.Skill.Repository.configure() for _,v in pairs(MuMeM.Skill.DataStore.skills) do MuMeM.Skill.Repository._skills[v.uid] = MuMeM.Skill.Skill:new() MuMeM.Skill.Repository._skills[v.uid]:update(v) end end
-- ###########################################################################
function MuMeM.Skill.Repository.getSkill(uid) return MuMeM.Skill.Repository._skills[uid] end
function MuMeM.Skill.Repository.getClassSkills(class) local list = {}
for k,v in pairs(MuMeM.Skill.DataStore.skills) do if v.class == class then local skill = MuMeM.Skill.Skill:new() skill:update(v) table.insert(list, skill) end end
return list end
function MuMeM.Skill.Repository.getSortedClassSkills(class) local list = {}
for k,v in pairs(MuMeM.Skill.DataStore.skills) do if v.class == class then local skill = MuMeM.Skill.Skill:new() skill:update(v) table.insert(list, skill) end end
table.sort(list, function(a,b) return a:name() > b:name() end)
return list end

