function MuMeM.Widget.BorderImage:setImage(image) MuMeM.Widget.BorderImage.parentClass.setImage(self, image)
local styleSheet = Geyser.StyleSheet:new(self._visuals.image.stylesheet or "") styleSheet:set("border-image", string.format("url(%s) 0 0 0 0 stretch stretch", self:image())) if not styleSheet:get("background-color") then styleSheet:set("background-color", "transparent") end self._visuals.image:setStyleSheet(styleSheet:getCSS()) end

